import React, { useEffect, useState } from 'react';

export default function Contacts() {
  const [contacts, setContacts] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [error, setError] = useState('');
  const [editId, setEditId] = useState(null);
  const [editName, setEditName] = useState('');
  const [editEmail, setEditEmail] = useState('');
  const [editPhone, setEditPhone] = useState('');

  const token = localStorage.getItem('token');

  // Récupérer la liste des contacts
  useEffect(() => {
    fetch('http://localhost:5000/contacts', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setContacts(data))
      .catch(() => setError('Erreur de chargement'));
  }, [token]);

  // Ajouter un contact
  const handleAdd = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await fetch('http://localhost:5000/contacts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ name, email, phone })
      });
      if (res.ok) {
        const newContact = await res.json();
        setContacts([...contacts, newContact]);
        setName(''); setEmail(''); setPhone('');
      } else {
        setError('Erreur lors de l\'ajout');
      }
    } catch {
      setError('Erreur serveur');
    }
  };

  // Supprimer un contact
  const handleDelete = async (id) => {
    setError('');
    try {
      const res = await fetch(`http://localhost:5000/contacts/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      if (res.ok) {
        setContacts(contacts.filter(c => c._id !== id));
      } else {
        setError('Erreur lors de la suppression');
      }
    } catch {
      setError('Erreur serveur');
    }
  };

  // Préparer l'édition
  const startEdit = (contact) => {
    setEditId(contact._id);
    setEditName(contact.name);
    setEditEmail(contact.email);
    setEditPhone(contact.phone);
  };

  // Valider l'édition
  const handleEdit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await fetch(`http://localhost:5000/contacts/${editId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ name: editName, email: editEmail, phone: editPhone })
      });
      if (res.ok) {
        const updated = await res.json();
        setContacts(contacts.map(c => c._id === editId ? updated : c));
        setEditId(null); setEditName(''); setEditEmail(''); setEditPhone('');
      } else {
        setError('Erreur lors de la modification');
      }
    } catch {
      setError('Erreur serveur');
    }
  };

  return (
    <div>
      <h2>Contacts</h2>
      {error && <p style={{color:'red'}}>{error}</p>}
      <form onSubmit={handleAdd}>
        <input
          type="text"
          placeholder="Nom"
          value={name}
          onChange={e => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Téléphone"
          value={phone}
          onChange={e => setPhone(e.target.value)}
          required
        />
        <button type="submit">Ajouter</button>
      </form>
      <ul>
        {contacts.map(contact => (
          <li key={contact._id}>
            {editId === contact._id ? (
              <form onSubmit={handleEdit}>
                <input value={editName} onChange={e => setEditName(e.target.value)} required />
                <input value={editEmail} onChange={e => setEditEmail(e.target.value)} required />
                <input value={editPhone} onChange={e => setEditPhone(e.target.value)} required />
                <button type="submit">Valider</button>
                <button type="button" onClick={() => setEditId(null)}>Annuler</button>
              </form>
            ) : (
              <>
                <span>{contact.name} - {contact.email} - {contact.phone}</span>
                <button onClick={() => startEdit(contact)}>Éditer</button>
                <button onClick={() => handleDelete(contact._id)}>Supprimer</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
